/* === checksum ========== */
unsigned int checksum(const void *buf, unsigned int size)
{
	const unsigned char *p;
	unsigned int sum = 0;
	unsigned char tmp;
	p = (const unsigned char *)buf;


	while (size--)
	{
		tmp = *p++;
		if( tmp== 0)  tmp = 1;
		sum += tmp;
	}
	return sum;
}

