						DMA_TBL.frame_burst_num=VIP_DemuraTBL_frame_burst_num_12bit_Y_481x271_3Plane;
						tbl_frame_size=VIP_DemuraTBL_frame_burst_num_12bit_Y_481x271_3Plane<<4;
