		case GAMMA_CURVE_9:
				*In_R = gVip_Table->tGAMMA[9].R;
				*In_G = gVip_Table->tGAMMA[9].G;
				*In_B = gVip_Table->tGAMMA[9].B;
				break;
		case GAMMA_CURVE_identity:
		default:
				*In_R = Identity_Gamma;
				*In_G = Identity_Gamma;
