			IoReg_ClearBits(VGIP_VGIP_CHN1_CTRL_reg, _BIT24|_BIT25);
		else
			modestate_EnableVGIPInt(FALSE);

#if IS_ENABLED(CONFIG_RTK_AI_DRV)
		fw_scalerip_reset_NN();
#endif

		//DI control setting
		down(get_DI_semaphore());
		fw_scalerip_set_di();
		up(get_DI_semaphore());

