				down(&SetMainOutPutRegion_Semaphore);
				DbgSclrFlgTkr.Main_Scaler_Stop_flag = TRUE;
				DbgSclrFlgTkr.Main_force_frc_flag = FALSE;
				DbgSclrFlgTkr.Main_force_frc_fs_flag = FALSE;
				DbgSclrFlgTkr.smoothtoggle_start_flag = FALSE;
