			break;
		case KADP_VIDEO_DDI_3D_PATTERN_SIDEBYSIDE_HALF:
			curmode = SLR_3DMODE_3D_SBS;
			break;
		case KADP_VIDEO_DDI_3D_PATTERN_LINE_INTERLEAVE_HALF:
