	static int isOpend=0;
	static int step=0;
	// subpath
	KADP_VSC_INPUT_SRC_INFO_T InputsourceInfo = {KADP_VSC_INPUTSRC_MAXN, 0 , 0};
	KADP_VIDEO_RECT_T  outregion;
	VIDEO_RECT_T inregion,oriregion;
